type EasternConferenceTeamName = (typeof easternConferenceTeams)[number];
type WesternConferenceTeamName = (typeof westernConferenceTeams)[number];
type TeamName = EasternConferenceTeamName | WesternConferenceTeamName;

type Record = `${number}-${number}`;

type Conference = "Eastern" | "Western";

const getConferenceByTeamName = (teamName: TeamName): Conference => {
  if (easternConferenceTeams.some((team) => team === teamName))
    return "Eastern";
  if (westernConferenceTeams.some((team) => team === teamName))
    return "Western";
  throw `Could not find conference for team ${teamName}`;
};

const getWinsAndLossesFromRecord = (record: Record) =>
  record.split("-").map((val) => parseInt(val, 10)) as [number, number];

const getSeasonData = (season: number, adjustmentWeight: number) => {
  const rawSeasonData = getRawSeasonDataByYear(season);

  const parsedData = JSON.parse(rawSeasonData) as {
    Team: TeamName;
    Overall: Record;
    E: Record;
    W: Record;
  }[];

  const teams = parsedData.map((data) => {
    const teamName = data.Team;
    const shortTeamName = getTeamShortName(teamName);
    const conference = getConferenceByTeamName(teamName);

    const [overallWins, overallLosses] = getWinsAndLossesFromRecord(
      data.Overall,
    );
    const [winsVsEasternConference, lossesVsEasternConference] =
      getWinsAndLossesFromRecord(data.E);
    const [winsVsWesternConference, lossesVsWesternConference] =
      getWinsAndLossesFromRecord(data.W);

    const gamesPlayed = overallWins + overallLosses;

    const winPercentage = (overallWins / gamesPlayed) * 100;

    const gamesPlayedVsEasternConference =
      winsVsEasternConference + lossesVsEasternConference;
    const gamesPlayedVsWesternConference =
      winsVsWesternConference + lossesVsWesternConference;

    const winPercentageVsEasternConference =
      winsVsEasternConference / gamesPlayedVsEasternConference;
    const winPercentageVsWesternConference =
      winsVsWesternConference / gamesPlayedVsWesternConference;

    return {
      teamName,
      shortTeamName,
      conference,
      overallWins,
      overallLosses,
      gamesPlayed,
      winPercentage,
      winsVsEasternConference,
      lossesVsEasternConference,
      winsVsWesternConference,
      lossesVsWesternConference,
      winPercentageVsEasternConference,
      winPercentageVsWesternConference,
    };
  });

  const interConferenceRecords = teams.reduce(
    (acc, val) => {
      if (val.conference === "Eastern") return acc;

      return {
        totalInterConferenceGamesPlayed:
          acc.totalInterConferenceGamesPlayed +
          val.winsVsEasternConference +
          val.lossesVsEasternConference,
        easternConferenceWins:
          acc.easternConferenceWins + val.lossesVsEasternConference,
        westernConferenceWins:
          acc.westernConferenceWins + val.winsVsEasternConference,
      };
    },
    {
      totalInterConferenceGamesPlayed: 0,
      easternConferenceWins: 0,
      westernConferenceWins: 0,
    },
  );

  const easternConferenceWinValue = Math.pow(
    (2 * interConferenceRecords.easternConferenceWins) /
      interConferenceRecords.totalInterConferenceGamesPlayed,
    adjustmentWeight,
  );

  const westernConferenceWinValue = Math.pow(
    (2 * interConferenceRecords.westernConferenceWins) /
      interConferenceRecords.totalInterConferenceGamesPlayed,
    adjustmentWeight,
  );

  const easternConferenceLossValue = 2 - (1 + easternConferenceWinValue) / 2;
  const westernConferenceLossValue = 2 - (1 + westernConferenceWinValue) / 2;

  const interConferenceStatistics = {
    ...interConferenceRecords,
    easternConferenceWinValue,
    westernConferenceWinValue,
    easternConferenceLossValue,
    westernConferenceLossValue,
  };

  const teamsWithAdjustedStatistics = teams.map((team) => {
    const adjustedWins =
      team.winsVsEasternConference * easternConferenceWinValue +
      team.winsVsWesternConference * westernConferenceWinValue;

    const adjustedLosses =
      team.lossesVsEasternConference * easternConferenceLossValue +
      team.lossesVsWesternConference * westernConferenceLossValue;

    const adjustedWinPercentage =
      (adjustedWins / (adjustedWins + adjustedLosses)) * 100;

    return {
      ...team,
      adjustedWins,
      adjustedLosses,
      adjustedWinPercentage,
    };
  });

  const teamsWithRankings = teamsWithAdjustedStatistics
    .sort((a, b) => b.winPercentage - a.winPercentage)
    .map((team, index) => ({
      ...team,
      ranking: index + 1,
    }))
    .sort((a, b) =>
      a.adjustedWinPercentage === b.adjustedWinPercentage
        ? a.ranking - b.ranking
        : b.adjustedWinPercentage - a.adjustedWinPercentage,
    )
    .map((team, index) => ({
      ...team,
      adjustedRanking: index + 1,
    }))
    .map((team) => ({
      ...team,
      rankingChange: team.ranking - team.adjustedRanking,
    }));

  return {
    teams: teamsWithRankings,
    interConferenceStatistics,
  };
};

type InterConferenceStatistics = ReturnType<
  typeof getSeasonData
>["interConferenceStatistics"];

type Teams = ReturnType<typeof getSeasonData>["teams"];

export default getSeasonData;
export type { InterConferenceStatistics, Teams };
